import asyncio#from machine import Pin#green_led_pin = 14#green_led = Pin(green_led_pin, Pin.OUT)#blue_led_pin = 12#blue_led = Pin(blue_led_pin, Pin.OUT)# Define coroutine functionasync def print1():    while True:        print("1")        await asyncio.sleep(2) # Define coroutine functionasync def print2():    while True:        print("2")        await asyncio.sleep(0.5)# Define the main function to run the event loopasync def main():    # Create tasks for blinking two LEDs concurrently    asyncio.create_task(print1())    asyncio.create_task(print2())# Create and run the event loop#loop = asyncio.get_event_loop()  #loop.create_task(main())  # Create a task to run the main function#loop.run_forever()  # Run the event loop indefinitelyasyncio.run(main())